        add {
            feature {
                id = "KEEP_RULE_1"
                type = "keepRules"
                param("limit.type", "all")
                param("keepData.1.type", "everything")
                param("ruleDisabled", "false")
                param("preserveArtifacts", "true")
            }
        }
